mapper.insert:
insert into qna_board(
   qna_title, 
   qna_content,
   qna_userid
) values (
   #{qna_title}, 
   #{qna_content},
   #{qna_userid}
)

mapper.update:
update qna_board 
   set qna_title = #{qna_title}, 
         qna_content = #{qna_content}, 
         qna_userid = #{qna_userid}
 where qna_no = #{qna_no}
